            // STATE
            let currentBuildType = '';
            let selections = {};

            // INITIALIZE BUILDER
            function initBuilder(type) {
                currentBuildType = type;
                selections = {};

                // UI Update
                document.getElementById('view-selection').classList.add('hidden-view');
                document.getElementById('view-builder').classList.remove('hidden-view');
                document.getElementById('view-review').classList.add('hidden-view');

                document.getElementById('builder-title').innerText =
                    `${type === 'pc' ? 'Personal Workstation' : 'Enterprise Infrastructure'}`;
                document.getElementById('platform-badge').innerText = type.toUpperCase();
                document.getElementById('platform-badge').className =
                    `px-4 py-2 rounded-full text-xs font-bold uppercase tracking-widest border ${type === 'pc' ? 'text-stone-600 border-stone-600' : 'text-green-400 border-green-500/30'}`;

                document.getElementById('bottom-bar').classList.remove('translate-y-full');

                renderComponentList();
                updateTotal();
                window.scrollTo({
                    top: 0,
                    behavior: 'smooth'
                });
            }

            function resetBuilder() {
                document.getElementById('view-selection').classList.remove('hidden-view');
                document.getElementById('view-builder').classList.add('hidden-view');
                document.getElementById('view-review').classList.add('hidden-view');
                document.getElementById('bottom-bar').classList.add('translate-y-full');
                selections = {};
                updateTotal();
                window.scrollTo({
                    top: 0,
                    behavior: 'smooth'
                });
            }

            // RENDER COMPONENT ROWS
            function renderComponentList() {
                const container = document.getElementById('component-list');
                container.innerHTML = '';
                const categories = partsData[currentBuildType];

                categories.forEach(category => {
                    const selectedPart = selections[category.id];
                    const div = document.createElement('div');
                    div.className =
                        "group bg-[#e5effa] p-5 rounded-2xl flex items-center justify-between cursor-pointer transition-all duration-200 hover:bg-[#e5effa]/60";
                    div.onclick = () => openModal(category.id);

                    div.innerHTML = `   
                    <div class="flex items-center gap-5">
                        ${selectedPart ? 
                            `<img src="${selectedPart.img}" class="w-16 h-16 rounded-xl object-cover border border-gray-700 shadow-md">` : 
                            `<div class="w-16 h-16 rounded-xl flex items-center justify-center text-3xl border border-gray-700 group-hover:border-blue-500/30 transition-colors">${category.icon}</div>`
                        }
                        <div>
                            <h4 class="font-bold text-gray-400 text-[10px] uppercase tracking-widest">${category.name}</h4>
                            <p class="${selectedPart ? 'text-gray-400' : 'text-gray-600 italic'} font-bold text-lg leading-tight">
                                ${selectedPart ? selectedPart.name : 'Choose Component'}
                            </p>
                        </div>
                    </div>
                    <div class="text-right flex items-center gap-6">
                        <div class="hidden sm:block">
                            <p class="font-mono text-xl ${selectedPart ? 'text-blue-500' : 'text-gray-800'}">${selectedPart ? '$' + selectedPart.price.toLocaleString() : '--'}</p>
                        </div>
                        <div class="w-8 h-8 rounded-full border border-gray-700 flex items-center justify-center group-hover:border-blue-500 group-hover:text-blue-500 transition-colors">
                            <svg class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7"></path></svg>
                        </div>
                    </div>
                `;
                    container.appendChild(div);
                });

                updateProgressDots();
            }

            function updateProgressDots() {
                const dotContainer = document.getElementById('selection-count');
                dotContainer.innerHTML = '';
                const categories = partsData[currentBuildType];

                categories.forEach(cat => {
                    const dot = document.createElement('div');
                    const isSelected = !!selections[cat.id];
                    dot.className =
                        `w-2 h-2 rounded-full transition-all duration-300 ${isSelected ? 'bg-blue-500 w-5' : 'bg-gray-700'}`;
                    dotContainer.appendChild(dot);
                });
            }

            // MODAL LOGIC
            function openModal(categoryId) {
                const category = partsData[currentBuildType].find(c => c.id === categoryId);
                document.getElementById('modal-title').innerText = `Select ${category.name}`;
                const optionsContainer = document.getElementById('modal-options');
                optionsContainer.innerHTML = '';

                category.options.forEach(option => {
                    const isSelected = selections[categoryId]?.name === option.name;
                    const card = document.createElement('div');
                    card.className =
                        `group bg-gray-800 border-2 rounded-2xl overflow-hidden cursor-pointer transition-all ${isSelected ? 'border-blue-500' : 'border-transparent hover:border-gray-600'}`;
                    card.onclick = () => selectPart(categoryId, option);

                    card.innerHTML = `
                    <div class="relative h-32">
                        <img src="${option.img}" class="w-full h-full object-cover opacity-60 group-hover:opacity-100 transition-opacity">
                        <div class="absolute inset-0 bg-gradient-to-t from-gray-900 to-transparent"></div>
                        <div class="absolute bottom-3 left-3 right-3 flex justify-between items-end">
                            <span class="text-xs font-black bg-blue-600 px-2 py-1 rounded">STOCK AVAILABLE</span>
                            <span class="font-mono font-bold text-lg text-white">$${option.price.toLocaleString()}</span>
                        </div>
                    </div>
                    <div class="p-4 bg-gray-800/80">
                        <h4 class="font-bold text-gray-100 line-clamp-1">${option.name}</h4>
                        <p class="text-[10px] text-gray-500 mt-1 uppercase tracking-widest">High Reliability Component</p>
                    </div>
                `;
                    optionsContainer.appendChild(card);
                });

                document.getElementById('modal').classList.remove('hidden');
                document.getElementById('modal').classList.add('modal-active');
            }

            function closeModal() {
                document.getElementById('modal').classList.add('hidden');
                document.getElementById('modal').classList.remove('modal-active');
            }

            function selectPart(categoryId, part) {
                selections[categoryId] = part;
                renderComponentList();
                updateTotal();
                closeModal();
            }

            // PRICE CALCULATION
            function updateTotal() {
                const total = Object.values(selections).reduce((sum, item) => sum + item.price, 0);
                document.getElementById('total-price').innerText = `$${total.toLocaleString()}`;

                const totalRequired = partsData[currentBuildType]?.length || 0;
                const currentSelected = Object.keys(selections).length;

                const btn = document.getElementById('review-btn');
                btn.disabled = currentSelected < totalRequired;
                btn.innerHTML = currentSelected < totalRequired ? `Configure ${currentSelected}/${totalRequired}` :
                    'Finalize Review';
            }

            function showReview() {
                document.getElementById('view-builder').classList.add('hidden-view');
                document.getElementById('view-review').classList.remove('hidden-view');
                document.getElementById('bottom-bar').classList.add('translate-y-full');

                const summary = document.getElementById('summary-content');
                summary.innerHTML = `<div class="mb-6 pb-4 border-b border-gray-700 flex justify-between items-center">
                <span class="text-gray-400 font-black uppercase tracking-widest text-[10px]">Architecture Spec Sheet</span>
                <span class="bg-blue-600/20 text-blue-400 px-3 py-1 rounded-full text-xs font-bold uppercase tracking-widest border border-blue-500/30">${currentBuildType} platform</span>
            </div>`;

                Object.entries(selections).forEach(([key, part]) => {
                    const cat = partsData[currentBuildType].find(c => c.id === key);
                    summary.innerHTML += `
                    <div class="flex justify-between items-center py-4 border-b border-gray-800/50 last:border-0">
                        <div class="flex items-center gap-4">
                            <img src="${part.img}" class="w-12 h-12 rounded-lg object-cover shadow-lg border border-gray-700">
                            <div>
                                <p class="text-[9px] text-gray-500 uppercase font-black tracking-widest">${cat.name}</p>
                                <p class="font-bold text-gray-200">${part.name}</p>
                            </div>
                        </div>
                        <p class="font-mono text-gray-400 text-sm">$${part.price.toLocaleString()}</p>
                    </div>
                `;
                });

                const total = Object.values(selections).reduce((sum, item) => sum + item.price, 0);
                summary.innerHTML += `
                <div class="mt-8 pt-6 border-t border-gray-700 flex justify-between items-center">
                    <div>
                        <p class="text-[10px] text-gray-500 font-black uppercase tracking-widest">Total Investment</p>
                        <span class="text-3xl font-black text-blue-500 leading-none">$${total.toLocaleString()}</span>
                    </div>
                    <button class="bg-blue-600 hover:bg-blue-500 text-white px-6 py-3 rounded-xl font-bold transition shadow-lg shadow-blue-500/20">Checkout</button>
                </div>
            `;
                window.scrollTo({
                    top: 0,
                    behavior: 'smooth'
                });
            }

            // Initialize state
            window.onload = () => {
                document.getElementById('bottom-bar').classList.add('translate-y-full');
            };
        </script>
    @endpush
@endsection
